mapper.select:
SELECT COUNT(*) 
FROM m_crawling
WHERE site = #{site}

mapper.delete:
    DELETE FROM m_crawling
	WHERE crawlingseq IN (
	  SELECT crawlingseq
	  FROM (
	    SELECT crawlingseq, ROW_NUMBER() OVER (ORDER BY created_at DESC) AS row_num
	    FROM m_crawling
	    WHERE site = #{site}
	  )
	  WHERE row_num > (SELECT COUNT(*) - 20 FROM m_crawling WHERE site=#{site})
	)
